import React from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";
import {
  AppBar,
  Toolbar,
  IconButton,
  Button,
  Typography,
  Box,
  Container,
  Grid,
} from "@mui/material";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import Popover from "@mui/material/Popover";
import blitzLogo from "../../Components/globalAssets/blitzLogo.png";
import imageOnRight from "../../Components/globalAssets/win.gif";
import Divider from "@mui/material/Divider";
import routes from "../../Config/routes";

const Home = () => {
  const navigate = useNavigate();
  const handleSignUp = () => navigate(routes.login);

  const [benefitsAnchorEl, setBenefitsAnchorEl] = React.useState(null);
  const [resourcesAnchorEl, setResourcesAnchorEl] = React.useState(null);

  const handleBenefitsPopoverOpen = (event) => {
    setBenefitsAnchorEl(event.currentTarget);
  };

  const handleBenefitsPopoverClose = () => {
    setBenefitsAnchorEl(null);
  };

  const handleResourcesPopoverOpen = (event) => {
    setResourcesAnchorEl(event.currentTarget);
  };

  const handleResourcesPopoverClose = () => {
    setResourcesAnchorEl(null);
  };

  return (
    <>
      {/* header */}
      <AppBar position="static" style={{ backgroundColor: "#d30303" }}>
        <Toolbar>
          <Box display="flex" flexGrow={1}>
            <IconButton edge="start" color="inherit" aria-label="logo">
              <img
                src={blitzLogo}
                alt="logo"
                style={{ width: "120px", height: "50px" }}
              />
            </IconButton>
          </Box>
          <Box
            display="flex"
            flexGrow={1}
            justifyContent="center"
            style={{ flexGrow: 2 }}
          >
            <Typography
              variant="h6"
              className="menu-item"
              aria-owns={
                Boolean(benefitsAnchorEl) ? "mouse-over-popover" : undefined
              }
              aria-haspopup="true"
              onMouseEnter={handleBenefitsPopoverOpen}
              onMouseLeave={handleBenefitsPopoverClose}
            >
              Benefits
            </Typography>
            <Popover
              id="mouse-over-popover"
              open={Boolean(benefitsAnchorEl)}
              anchorEl={benefitsAnchorEl}
              anchorOrigin={{
                vertical: "bottom",
                horizontal: "left",
              }}
              transformOrigin={{
                vertical: "top",
                horizontal: "left",
              }}
              onClose={handleBenefitsPopoverClose}
              disableRestoreFocus
            >
              <Menu>
                <MenuItem onClick={handleBenefitsPopoverClose}>
                  Benefit 1
                </MenuItem>
                <MenuItem onClick={handleBenefitsPopoverClose}>
                  Benefit 2
                </MenuItem>
                <MenuItem onClick={handleBenefitsPopoverClose}>
                  Benefit 3
                </MenuItem>
              </Menu>
            </Popover>

            <Typography
              variant="h6"
              style={{ marginLeft: "2.5%", marginRight: "2.5%" }}
              className="menu-item"
              onClick={() => navigate(routes.resources)}
            >
              Corporate
            </Typography>
            <Typography
              variant="h6"
              className="menu-item"
              aria-owns={
                Boolean(benefitsAnchorEl) ? "mouse-over-popover" : undefined
              }
              aria-haspopup="true"
              onMouseEnter={handleResourcesPopoverOpen}
              onMouseLeave={handleResourcesPopoverClose}
            >
              Resources
            </Typography>
            <Popover
              id="mouse-over-popover"
              open={Boolean(resourcesAnchorEl)}
              anchorEl={resourcesAnchorEl}
              anchorOrigin={{
                vertical: "bottom",
                horizontal: "left",
              }}
              transformOrigin={{
                vertical: "top",
                horizontal: "left",
              }}
              onClose={handleResourcesPopoverClose}
              disableRestoreFocus
            >
              <Menu>
                <MenuItem onClick={handleResourcesPopoverClose}>
                  Resource 1
                </MenuItem>
                <MenuItem onClick={handleResourcesPopoverClose}>
                  Resource 2
                </MenuItem>
                <MenuItem onClick={handleResourcesPopoverClose}>
                  Resource 3
                </MenuItem>
              </Menu>
            </Popover>

            <Typography
              variant="h6"
              style={{ marginLeft: "2.5%", marginRight: "2.5%" }}
              className="menu-item"
              onClick={() => navigate(routes.about)}
            >
              About
            </Typography>
          </Box>

          <Box style={{ flexGrow: 1 }}></Box>
          <Button color="inherit" onClick={handleSignUp}>
            Login
          </Button>
        </Toolbar>
      </AppBar>

      {/* Main content section */}
      <Container maxWidth="false" className="main-content">
        <Grid container spacing={3}>
          {/* Left Column */}
          <Grid item xs={12} md={6}>
            <Typography
              variant="h1"
              gutterBottom
              style={{ marginTop: "50px", marginBottom: "100px" }}
            >
              <span className="red-text">Blitz</span> your way into{" "}
              <span className="red-underline"> inspiring global culture</span>
            </Typography>

            <Typography variant="h3" gutterBottom>
              Break free from the platforms of the past. Power the future of
              influencer marketing today
            </Typography>
            <div>
              <Button
                variant="contained"
                color="primary"
                style={{ marginRight: "10px" }}
              >
                Read More
              </Button>
              <Button variant="contained" color="secondary">
                Request Demo
              </Button>
            </div>
          </Grid>

          {/* Right Column */}
          <Grid
            item
            xs={12}
            md={6}
            style={{ display: "flex", justifyContent: "center" }}
          >
            <img
              src={imageOnRight}
              alt="description"
              style={{ height: "72%", width: "50%", objectFit: "contain" }}
            />
          </Grid>

          {/* Divider */}
          <Grid item xs={12}>
            <Divider />
          </Grid>

          {/* Second Row */}
          {/* Second Row - Column 1 */}
          <Grid item xs={12} md={4} className="vertical-divider">
            <Typography variant="h3" gutterBottom>
              5,000,000+ in Transaction Volume
            </Typography>
          </Grid>

          {/* Second Row - Column 2 */}
          <Grid item xs={12} md={4} className="vertical-divider">
            <Typography variant="h3" gutterBottom>
              3-Point Data Encryption
            </Typography>
          </Grid>

          {/* Second Row - Column 3 */}
          <Grid item xs={12} md={4}>
            <Typography variant="h3" gutterBottom>
              Automating Vendor Processing Across the world
            </Typography>
          </Grid>
        </Grid>
      </Container>

      <footer
        style={{
          backgroundColor: "#f5f5f5",
          padding: "20px",
          textAlign: "center",
        }}
      >
        <Typography variant="body2" color="textSecondary">
          Â© 2023 Pangea, Inc. All rights reserved.
        </Typography>
      </footer>
    </>
  );
};
export default Home;
